import {useEffect, useState} from "react";
import {Link} from "react-router-dom";
import Modal from "./Modal";

function Users() {

    useEffect(() => {
        fetchItems();
    }, []);

    const [users, setUsers] = useState([]);

    const fetchItems = async () => {
        const users_data = await fetch(
            'https://jsonplaceholder.typicode.com/users'
        );

        const todos_data = await fetch(
          'https://jsonplaceholder.typicode.com/todos'
        );

        const users = await users_data.json();
        const todos = await todos_data.json();

        let userId;
        for(let i = 0; i < users.length; i++) {
            userId = users[i]['id'];
            users[i]['todos'] = [];
            console.log(users[i]);
            for(let j = 0; j < todos.length; j++) {
                if(todos[j]['userId'] === userId) {
                    users[i].todos.push(todos[j])
                }
            }
        }

        setUsers(users);
    }

    return (
        <div>
            <h1 className="mb-4">Users page</h1>

            <table className="table table-stripped">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Username</th>
                        <th>Email</th>
                        <th>Todo count</th>
                        <th>Details</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.username}</td>
                            <td>{user.email}</td>
                            <td>{user.todos.length} todos</td>
                            <td>
                                <Link to={`/user/${user.id}`}>
                                    <span className="btn btn-warning">More</span>
                                </Link>
                                &nbsp;
                                <Modal user={user} />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Users;
