import logo from './logo.svg';
import './App.css';
import SecondApi from "./SecondApi"
import {useEffect, useState} from "react";
import {Link} from "react-router-dom";

function App() {
  /*
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
  */
 useEffect(() => {
  fetchItems();
}, []);

const [users, setUsers] = useState([]);

const fetchItems = async () => {
  const users_data = await fetch(
      'https://jsonplaceholder.typicode.com/users'
  );

  const todos_data = await fetch(
    'https://jsonplaceholder.typicode.com/todos'
  );

  const users = await users_data.json();
  const todos = await todos_data.json();

  let userId;
  for(let i = 0; i < users.length; i++) {
      userId = users[i]['id'];
      users[i]['todos'] = [];
      console.log(users[i]);
      for(let j = 0; j < todos.length; j++) {
          if(todos[j]['userId'] === userId) {
              users[i].todos.push(todos[j])
          }
      }
  }

  setUsers(users);
}

return (
  <div>
      <h1 className="mb-4">Users page</h1>

      <table className="table table-stripped">
          <thead>
              <tr>
                  <th>#</th>
                  <th>Username</th>
                  <th>Email</th>
                  <th>Todo count</th>
                  <th>Details</th>
              </tr>
          </thead>
          <tbody>
              {users.map(user => (
                  <tr key={user.id}>
                      <td>{user.id}</td>
                      <td>{user.username}</td>
                      <td>{user.email}</td>
                      <td>{user.todos.length} todos</td>
                  </tr>
              ))}
          </tbody>
      </table>
  </div>
);
}

export default App;
