import {useEffect, useState} from "react";

function Users() {

    useEffect(() => {
        fetchItems();
    }, []);

    const [users, setUsers] = useState([]);

    const fetchItems = async () => {
        const url = 'https://api.jikan.moe/v3/search/anime?limit=1&q='
        const param = search;
        const response = await fetch(`${url}/${param}`);
        const result = await response.json();

        const users = await users_data.json();
        const todos = await todos_data.json();

        let userId;
        for(let i = 0; i < users.length; i++) {
            userId = users[i]['id'];
            users[i]['todos'] = [];
            console.log(users[i]);
            for(let j = 0; j < todos.length; j++) {
                if(todos[j]['userId'] === userId) {
                    users[i].todos.push(todos[j])
                }
            }
        }

        setUsers(users);
    }

    return (
        <div>
            <h1 className="mb-4">Users page</h1>

            <table className="table table-stripped">
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Username</th>
                        <th>Email</th>
                        <th>Todo count</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.id}</td>
                            <td>{user.username}</td>
                            <td>{user.email}</td>
                            <td>{user.todos.length} todos</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default Users;
